+                                        .css(btn, btnDefault)
+                                        .on(click, event -> {
+                                            showValue(number);
+                                            modifyValue(number);
+                                        })
+                                        .rememberAs(value)
+                                        .textContent(value)
+                                        .end();
+                                buttons.put(number, builder.referenceFor(value));
+                            }
+                        builder.end()
+                    .end()
