+                                            .param(PROXY, name)
+                                            .payload(modelNode)
+                                            .build();
+                                    dispatcher.execute(operation, result -> {
+                                        MessageEvent.fire(eventBus,
+                                                Message.success(resources.messages()
+                                                        .addResourceSuccess(Names.PROXY, name)));
+                                        column.refresh(Ids.modclusterProxy(name));
+                                    });
+                                }
+                            });
