+
+
+   @Override
+   public boolean isInitial()
+   {
+      return true;
+   }
+
+   public NettyHttpResponse getResponse()
+   {
+       return httpResponse;
+   }
+
+   public boolean isKeepAlive()
+   {
+       return httpResponse.isKeepAlive();
+   }
+
+   public boolean is100ContinueExpected()
+   {
+       return is100ContinueExpected;
