+public class FileResourceManager implements ResourceManager {
+
+    private volatile File base;
+
+    public FileResourceManager(final File base) {
+        if (base == null) {
+            throw UndertowMessages.MESSAGES.argumentCannotBeNull("base");
+        }
+        this.base = base;
+    }
+
+    public File getBase() {
+        return base;
