+            while (cb.hasRemaining()) {
+                int remaining = buffer.remaining();
+                CoderResult result = charsetEncoder.encode(cb, buffer, false);
+                outputStream.updateWritten(remaining - buffer.remaining());
+                if(result.isOverflow()) {
